import React, { useState } from "react";
import { useNavigate, Link } from "react-router-dom";
import { useAuth } from "../context/AuthContext";
import api from "../ap/api"; // ✅ import this

function Login() {
  const { login } = useAuth();
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const nav = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await api.post("/auth/login", { email, password });

      // backend sends { token, user }
      const { token, user } = response.data;

      localStorage.setItem("token", token);
      login(user);

      nav("/jobs");
    } catch (error) {
      alert("Invalid credentials. Please try again.");
    }
  };

  const loginBg = process.env.PUBLIC_URL + "/assets/login-bg.jpg";

  return (
    <div
      className="auth-page"
      style={{
        backgroundImage: `url(${loginBg})`,
        backgroundSize: "cover",
        backgroundPosition: "center",
      }}
    >
      <div className="auth-overlay">
        <div className="auth-container">
          <h2>Welcome Back</h2>
          <p>Sign in to access your account</p>
          <form onSubmit={handleSubmit}>
            <input
              type="email"
              placeholder="Email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
            />
            <input
              type="password"
              placeholder="Password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
            />
            <p className="auth-link" style={{ textAlign: "right" }}>
              <Link to="/forgot-password">Forgot Password?</Link>
            </p>
            <button type="submit" className="btn-primary full-btn">
              Sign In
            </button>
          </form>
          <p className="auth-link">
            Don’t have an account? <Link to="/register">Register</Link>
          </p>
        </div>
      </div>
    </div>
  );
}

export default Login;
