import React, { useEffect, useState } from "react";
import api from "../ap/api";

function EmployerDashboard() {
  const [jobs, setJobs] = useState([]);
  const [title, setTitle] = useState("");
  const [company, setCompany] = useState("");
  const [location, setLocation] = useState("");
  const [description, setDescription] = useState("");

  useEffect(() => loadJobs(), []);

  const loadJobs = () => {
    api.get("/jobs/my").then(res => setJobs(res.data));
  };

  const addJob = async () => {
    await api.post("/jobs/add", { title, company, location, description });
    loadJobs();
  };

  const deleteJob = async (id) => {
    await api.delete(`/jobs/delete/${id}`);
    loadJobs();
  };

  return (
    <div className="dashboard-page">

      <h2>Employer Dashboard</h2>

      <h3>Post New Job</h3>
      <input placeholder="Title" onChange={e => setTitle(e.target.value)} />
      <input placeholder="Company" onChange={e => setCompany(e.target.value)} />
      <input placeholder="Location" onChange={e => setLocation(e.target.value)} />
      <textarea placeholder="Description" onChange={e => setDescription(e.target.value)} />
      <button onClick={addJob} className="btn-primary">Post Job</button>

      <h3>My Posted Jobs</h3>
      {jobs.map(job => (
        <div key={job.id} className="job-card">
          <h4>{job.title}</h4>
          <p>{job.company} â€” {job.location}</p>
          <button className="btn-outline" onClick={() => deleteJob(job.id)}>Delete</button>
        </div>
      ))}
    </div>
  );
}

export default EmployerDashboard;
