import React from "react";
import { Link } from "react-router-dom";

function Home() {
  // Correct way to access images from public folder
  const heroBg = process.env.PUBLIC_URL + "/assets/hero-bg.jpg";

  return (
    <section
      className="hero-section"
      style={{
        backgroundImage: `url(${heroBg})`,
        backgroundSize: "cover",
        backgroundPosition: "center",
      }}
    >
      <div className="overlay">
        <div className="hero-content">
          <h1>Empowering Careers. Connecting Talent.</h1>
          <p>Discover jobs that match your passion and skills â€” all in one place.</p>
          <Link to="/login" className="btn-primary">
            Get Started
          </Link>
        </div>
      </div>
    </section>
  );
}

export default Home;
