// src/pages/Jobs.js
import React, { useEffect, useState } from "react";
import api from "../ap/api.js";

function Jobs() {
  const [jobs, setJobs] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    loadJobs();
  }, []);

  const loadJobs = async () => {
    try {
      const res = await api.get("/jobs");
      setJobs(res.data);
    } catch (err) {
      console.error("Failed to load jobs", err);
      setJobs([]);
    } finally {
      setLoading(false);
    }
  };

  const apply = async (id) => {
    try {
      await api.post(`/applications/apply/${id}`);
      alert("‚úÖ Successfully Applied!");
    } catch (e) {
      alert("‚ö†Ô∏è You must be logged in as an Applicant to apply.");
    }
  };

  const saveJob = async (id) => {
    try {
      await api.post(`/applications/save/${id}`);
      alert("üíæ Job Saved to Dashboard!");
    } catch (e) {
      alert("‚ö†Ô∏è Login first to save jobs.");
    }
  };

  if (loading)
    return (
      <div className="jobs-container">
        <h3>‚è≥ Loading Jobs...</h3>
      </div>
    );

  return (
    <div className="jobs-section">
      <div className="overlay">
        <div className="jobs-container">
          <h2>Available Jobs</h2>

          <div className="job-grid">
            {jobs.map((job) => (
              <div key={job.id} className="job-card">
                <h3>{job.title}</h3>
                <p>
                  <strong>{job.company}</strong> ‚Äî {job.location}
                </p>
                <p className="muted">{job.description}</p>

                <div className="job-actions">
                  <button className="btn-primary" onClick={() => apply(job.id)}>
                    Apply Now
                  </button>

                  <button className="btn-outline" onClick={() => saveJob(job.id)}>
                    Save Job
                  </button>
                </div>
              </div>
            ))}

            {jobs.length === 0 && <p>No jobs available right now.</p>}
          </div>
        </div>
      </div>
    </div>
  );
}

export default Jobs;
