// src/pages/ApplicantDashboard.jsx
import React, { useEffect, useState } from "react";
import api from "../ap/api";

export default function ApplicantDashboard() {
  const [apps, setApps] = useState([]);

  const load = async () => {
    const res = await api.get("/applications/me");
    setApps(res.data);
  };

  useEffect(() => { load(); }, []);

  return (
    <section className="dash">
      <div className="dash-header">
        <h2>My Applications</h2>
        <p>Track your saved and applied jobs</p>
      </div>

      <div className="card">
        <div className="job-grid">
          {apps.map(a => (
            <article key={a.id} className="job-card">
              <h4>{a.job?.title}</h4>
              <p className="muted">{a.job?.company} â€¢ {a.job?.location}</p>
              <span className={`badge ${a.type === "APPLIED" ? "b-green" : "b-blue"}`}>
                {a.type}
              </span>
            </article>
          ))}
          {apps.length === 0 && <p>No applications yet.</p>}
        </div>
      </div>
    </section>
  );
}
