import React, { useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import api from "../ap/api";

function Login() {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const nav = useNavigate();

  const handleLogin = async(e) => {
    e.preventDefault();
    try {
      const res = await api.post("/auth/login", { email, password });
      localStorage.setItem("token", res.data.token);
      nav("/jobs");
    } catch {
      alert("Invalid credentials.");
    }
  };

  return (
    <div className="auth-page" style={{ backgroundImage: `url('/assets/login-bg.jpg')` }}>
      <div className="auth-box">
        <h2>Welcome Back</h2>
        <form onSubmit={handleLogin}>
          <input type="email" placeholder="Email" value={email} onChange={(e)=>setEmail(e.target.value)} required />
          <input type="password" placeholder="Password" value={password} onChange={(e)=>setPassword(e.target.value)} required />
          <button className="btn-primary full-btn">Sign In</button>
        </form>
        <p>Donâ€™t have an account? <Link to="/register">Register</Link></p>
      </div>
    </div>
  );
}
export default Login;
