// src/pages/Jobs.jsx
import React, { useEffect, useState } from "react";
import api from "../ap/api";
import { useAuth } from "../context/AuthContext";

export default function Jobs() {
  const { user } = useAuth();
  const [jobs, setJobs] = useState([]);
  const [q, setQ] = useState("");
  const [loc, setLoc] = useState("");
  const [loading, setLoading] = useState(true);

  const load = async () => {
    setLoading(true);
    try {
      const params = {};
      if (q) params.q = q;
      if (loc) params.location = loc;
      const res = await api.get("/jobs", { params });
      setJobs(res.data);
    } catch {
      setJobs([]);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => { load(); }, []);

  const apply = async (id) => {
    try {
      await api.post(`/applications/apply/${id}`);
      alert("Applied successfully!");
    } catch {
      alert("Please login as Applicant to apply.");
    }
  };

  return (
    <section className="jobs-hero" style={{ backgroundImage: `url(${process.env.PUBLIC_URL}/assets/jobs-bg.jpg)` }}>
      <div className="overlay">
        <div className="jobs-toolbar">
          <input placeholder="Search roles or company" value={q} onChange={(e)=>setQ(e.target.value)} />
          <input placeholder="Location" value={loc} onChange={(e)=>setLoc(e.target.value)} />
          <button className="btn-primary" onClick={load}>Search</button>
        </div>

        <div className="jobs-container">
          <h2>Open Roles</h2>
          {loading && <p>Loading...</p>}

          <div className="job-grid">
            {jobs.map((job) => (
              <article key={job.id} className="job-card">
                <h3>{job.title}</h3>
                <p className="muted">{job.company} â€¢ {job.location}</p>
                <p className="desc">{job.description}</p>
                {user?.role === "applicant" && (
                  <button className="btn-outline" onClick={() => apply(job.id)}>Apply Now</button>
                )}
              </article>
            ))}
            {!loading && jobs.length === 0 && <p>No jobs found.</p>}
          </div>
        </div>
      </div>
    </section>
  );
}
