// src/pages/Register.jsx
import React, { useState } from "react";
import { useNavigate, Link } from "react-router-dom";
import api from "../ap/api";

export default function Register() {
  const [form, setForm] = useState({ name: "", email: "", password: "", role: "applicant" });
  const [loading, setLoading] = useState(false);
  const nav = useNavigate();

  const submit = async (e) => {
    e.preventDefault();
    try {
      setLoading(true);
      await api.post("/auth/register", form);
      alert("Registration successful. Please login.");
      nav("/login");
    } catch {
      alert("Registration failed. Try again.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <section className="auth-page" style={{ backgroundImage: `url(${process.env.PUBLIC_URL}/assets/register-bg.jpg)` }}>
      <div className="auth-overlay">
        <div className="auth-container">
          <h2>Create an Account</h2>
          <p>Join JobConnect</p>
          <form onSubmit={submit}>
            <input placeholder="Full Name" value={form.name}
                   onChange={(e) => setForm({ ...form, name: e.target.value })} required />
            <input type="email" placeholder="Email" value={form.email}
                   onChange={(e) => setForm({ ...form, email: e.target.value })} required />
            <input type="password" placeholder="Password" value={form.password}
                   onChange={(e) => setForm({ ...form, password: e.target.value })} required />
            <select value={form.role} onChange={(e) => setForm({ ...form, role: e.target.value })}>
              <option value="applicant">Applicant</option>
              <option value="employer">Employer</option>
            </select>
            <button className="btn-primary full-btn" disabled={loading}>
              {loading ? "Creating..." : "Register"}
            </button>
          </form>
          <p className="auth-link">
            Already have an account? <Link to="/login">Login</Link>
          </p>
        </div>
      </div>
    </section>
  );
}
