import React, { useEffect, useState } from "react";
import JobCard from "../components/JobCard";
import jobsBg from "../../public/assets/jobs-bg.jpg";

function Jobs() {
  const [jobs, setJobs] = useState([]);

  useEffect(() => {
    setJobs([
      { title: "Frontend Developer", company: "Accenture", location: "Hyderabad" },
      { title: "Java Backend Engineer", company: "TCS", location: "Pune" },
      { title: "Cloud DevOps Engineer", company: "Amazon", location: "Bangalore" },
    ]);
  }, []);

  return (
    <section className="jobs-section" style={{ backgroundImage: `url(${jobsBg})` }}>
      <div className="overlay">
        <div className="jobs-container">
          <h2>Explore Opportunities</h2>
          <div className="job-grid">
            {jobs.map((job, i) => <JobCard key={i} job={job} />)}
          </div>
        </div>
      </div>
    </section>
  );
}

export default Jobs;
