// src/pages/EmployerDashboard.jsx
import React, { useEffect, useState } from "react";
import api from "../ap/api";

export default function EmployerDashboard() {
  const empty = { title: "", company: "", location: "", description: "" };
  const [form, setForm] = useState(empty);
  const [mine, setMine] = useState([]);

  const load = async () => {
    const res = await api.get("/jobs/my");
    setMine(res.data);
  };
  useEffect(() => { load(); }, []);

  const submit = async (e) => {
    e.preventDefault();
    await api.post("/jobs/add", form);
    setForm(empty);
    load();
  };

  const update = async (id) => {
    const payload = prompt("Update as JSON (title, company, location, description):", JSON.stringify(form));
    if (!payload) return;
    await api.put(`/jobs/update/${id}`, JSON.parse(payload));
    load();
  };

  const remove = async (id) => {
    if (!window.confirm("Delete this job?")) return;
    await api.delete(`/jobs/delete/${id}`);
    load();
  };

  return (
    <section className="dash">
      <div className="dash-header">
        <h2>Employer Dashboard</h2>
        <p>Post new roles and manage your listings.</p>
      </div>

      <div className="card">
        <h3>Post a Job</h3>
        <form onSubmit={submit} className="grid2">
          <input placeholder="Title" value={form.title} onChange={e=>setForm({...form, title:e.target.value})} required />
          <input placeholder="Company" value={form.company} onChange={e=>setForm({...form, company:e.target.value})} required />
          <input placeholder="Location" value={form.location} onChange={e=>setForm({...form, location:e.target.value})} required />
          <textarea placeholder="Description" value={form.description} onChange={e=>setForm({...form, description:e.target.value})} />
          <button className="btn-primary" type="submit">Publish</button>
        </form>
      </div>

      <div className="card">
        <h3>Your Jobs</h3>
        <div className="job-grid">
          {mine.map(j => (
            <article key={j.id} className="job-card">
              <h4>{j.title}</h4>
              <p className="muted">{j.company} â€¢ {j.location}</p>
              <div className="row">
                <button className="btn-outline" onClick={() => update(j.id)}>Edit</button>
                <button className="btn-danger" onClick={() => remove(j.id)}>Delete</button>
              </div>
            </article>
          ))}
          {mine.length === 0 && <p>No jobs yet.</p>}
        </div>
      </div>
    </section>
  );
}
