import React, { useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import api from "../ap/api";

function Register() {
  const [form, setForm] = useState({ name:"", email:"", password:"", role:"applicant" });
  const nav = useNavigate();

  const submit = async(e)=>{
    e.preventDefault();
    try{
      await api.post("/auth/register", form);
      alert("Account created successfully!");
      nav("/login");
    }catch{
      alert("Registration failed.");
    }
  };

  return (
    <div className="auth-page" style={{ backgroundImage: `url('/assets/register-bg.jpg')` }}>
      <div className="auth-box">
        <h2>Create an Account</h2>
        <form onSubmit={submit}>
          <input placeholder="Full Name" value={form.name} onChange={e => setForm({...form,name:e.target.value})} required />
          <input placeholder="Email" type="email" value={form.email} onChange={e => setForm({...form,email:e.target.value})} required />
          <input placeholder="Password" type="password" value={form.password} onChange={e => setForm({...form,password:e.target.value})} required />
          <select value={form.role} onChange={(e)=>setForm({...form,role:e.target.value})}>
            <option value="applicant">Applicant</option>
            <option value="employer">Employer</option>
          </select>
          <button className="btn-primary full-btn">Register</button>
        </form>
        <p>Already have an account? <Link to="/login">Login</Link></p>
      </div>
    </div>
  );
}
export default Register;
