import React, { useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import api from "../ap/api";
import "../styles/register.css";

function Register() {
  const [form, setForm] = useState({ name:"", email:"", password:"", role:"applicant" });
  const nav = useNavigate();

  const submit = async(e)=>{
    e.preventDefault();
    try{
      await api.post("/auth/register", form);
      alert("Account created successfully! Please login.");
      nav("/login");
    }catch{
      alert("Registration failed, try again.");
    }
  };

  return (
    <div 
      className="register-page"
      style={{ backgroundImage: `url(${process.env.PUBLIC_URL}/assets/register-bg.jpg)` }}
    >
      <div className="register-card">
        <h2>Create an Account</h2>
        <p>Join and explore opportunities</p>

        <form onSubmit={submit}>
          <input 
            placeholder="Full Name"
            value={form.name}
            onChange={(e) => setForm({ ...form, name: e.target.value })}
            required
          />

          <input 
            placeholder="Email" type="email"
            value={form.email}
            onChange={(e) => setForm({ ...form, email: e.target.value })}
            required
          />

          <input 
            placeholder="Password" type="password"
            value={form.password}
            onChange={(e) => setForm({ ...form, password: e.target.value })}
            required
          />

          <select 
            value={form.role}
            onChange={(e)=>setForm({...form, role:e.target.value})}
          >
            <option value="applicant">Applicant</option>
            <option value="employer">Employer</option>
          </select>

          <button type="submit">Register</button>
        </form>

        <div className="switch-text">
          Already have an account? <Link to="/login" style={{color:"#fff", textDecoration:"underline"}}>Login</Link>
        </div>
      </div>
    </div>
  );
}

export default Register;
