// src/components/Navbar.js
import React, { useState } from "react";
import { Link, NavLink, useNavigate } from "react-router-dom";
import { useAuth } from "../context/AuthContext";
import NotificationDropdown from "./NotificationDropdown";
import "./Navbar.css";

const baseLinks = [{ to: "/jobs", label: "Jobs" }];

const applicantLinks = [
  { to: "/applicant/dashboard", label: "Dashboard" },
  { to: "/applicant/applications", label: "Applications" },
  { to: "/applicant/saved-jobs", label: "Saved Jobs" },
];

const employerLinks = [
  { to: "/employer/dashboard", label: "Dashboard" },
  { to: "/employer/post-job", label: "Post Job" },
  { to: "/employer/manage-jobs", label: "Manage Jobs" },
];

export default function Navbar() {
  const [menuOpen, setMenuOpen] = useState(false);
  const navigate = useNavigate();
  const { isAuthenticated, user, logout } = useAuth();

  const roleLinks = user?.role === "APPLICANT" ? applicantLinks : user?.role === "EMPLOYER" ? employerLinks : [];

  const handleLogout = () => {
    logout();
    setMenuOpen(false);
    navigate("/login");
  };

  const renderLinks = (links) =>
    links.map(({ to, label }) => (
      <NavLink
        key={to}
        to={to}
        className={({ isActive }) =>
          `nav-link${isActive ? " nav-link-active" : ""}`
        }
        onClick={() => setMenuOpen(false)}
      >
        {label}
      </NavLink>
    ));

  return (
    <header className="navbar">
      <div className="nav-container">
        <div className="nav-left">
          <Link to="/" className="nav-logo" onClick={() => setMenuOpen(false)}>
            <span className="nav-logo-accent">Job</span>Connect
          </Link>
        </div>

        <button
          type="button"
          className="nav-toggle"
          aria-label="Toggle menu"
          onClick={() => setMenuOpen((prev) => !prev)}
        >
          <span />
          <span />
          <span />
        </button>

        <nav className={`nav-menu${menuOpen ? " nav-menu-open" : ""}`}>
          <div className="nav-links">
            {renderLinks(baseLinks)}
            {isAuthenticated && renderLinks(roleLinks)}
          </div>

          <div className="nav-divider" aria-hidden="true" />

          <div className="nav-actions">
            {isAuthenticated ? (
              <>
                <NotificationDropdown />
                <NavLink
                  to="/profile"
                  className="nav-profile"
                  onClick={() => setMenuOpen(false)}
                >
                  <span className="nav-profile-label">{user?.name || "Profile"}</span>
                  <span className="nav-profile-role">{user?.role}</span>
                </NavLink>
                <button type="button" className="nav-button nav-button-outline" onClick={handleLogout}>
                  Logout
                </button>
              </>
            ) : (
              <>
                <Link to="/login" className="nav-button nav-button-outline" onClick={() => setMenuOpen(false)}>
                  Log in
                </Link>
                <Link to="/register" className="nav-button nav-button-primary" onClick={() => setMenuOpen(false)}>
                  Sign up
                </Link>
              </>
            )}
          </div>
        </nav>
      </div>
    </header>
  );
}
