import React, { useState } from "react";
import { Link } from "react-router-dom";

function ForgotPassword() {
  const [email, setEmail] = useState("");
  const [submitted, setSubmitted] = useState(false);
  const forgotBg = process.env.PUBLIC_URL + "/assets/login-bg.jpg"; // reuse login background

  const handleSubmit = (e) => {
    e.preventDefault();
    // TODO: later connect this to your Spring Boot endpoint
    // await axios.post("/api/auth/forgot-password", { email });
    setSubmitted(true);
  };

  return (
    <div
      className="auth-page"
      style={{
        backgroundImage: `url(${forgotBg})`,
        backgroundSize: "cover",
        backgroundPosition: "center",
      }}
    >
      <div className="auth-overlay">
        <div className="auth-container">
          {!submitted ? (
            <>
              <h2>Forgot Password</h2>
              <p>Enter your registered email to reset your password.</p>
              <form onSubmit={handleSubmit}>
                <input
                  type="email"
                  placeholder="Email Address"
                  value={email}
                  onChange={(e) => setEmail(e.target.value)}
                  required
                />
                <button type="submit" className="btn-primary full-btn">
                  Send Reset Link
                </button>
              </form>
              <p className="auth-link">
                Remembered? <Link to="/login">Go back to Login</Link>
              </p>
            </>
          ) : (
            <>
              <h2>Check Your Email</h2>
              <p>Weâ€™ve sent a password reset link to <strong>{email}</strong>.</p>
              <p>Please check your inbox (and spam folder).</p>
              <Link to="/login" className="btn-primary full-btn">
                Back to Login
              </Link>
            </>
          )}
        </div>
      </div>
    </div>
  );
}

export default ForgotPassword;
