// src/pages/Jobs.js
import React, { useEffect, useState } from "react";
import api from "../ap/api.js";

function Jobs() {
  const [jobs, setJobs] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    api.get("/jobs")
      .then(res => setJobs(res.data))
      .catch(() => setJobs([]))
      .finally(() => setLoading(false));
  }, []);

  const apply = async (id) => {
    try {
      await api.post(`/applications/apply/${id}`);
      alert("Applied!");
    } catch (e) {
      alert("Please login as Applicant to apply.");
    }
  };

  if (loading) return <div className="jobs-container"><h3>Loading...</h3></div>;

  return (
    <div className="jobs-section">
      <div className="overlay">
        <div className="jobs-container">
          <h2>Available Jobs</h2>
          <div className="job-grid">
            {jobs.map((job) => (
              <div key={job.id} className="job-card">
                <h3>{job.title}</h3>
                <p>{job.company} â€” {job.location}</p>
                <p className="muted">{job.description}</p>
                <button className="btn-outline" onClick={() => apply(job.id)}>
                  Apply Now
                </button>
              </div>
            ))}
            {jobs.length === 0 && <p>No jobs yet.</p>}
            <button
  className="btn-outline"
  onClick={() => api.post(`/jobs/apply/${job.id}?userId=${localStorage.getItem("userId")}`)
      .then(() => alert("Application Submitted!"))
      .catch(() => alert("Already applied or failed."))}
>
  Apply Now
</button>

          </div>
        </div>
      </div>
    </div>
  );
}

export default Jobs;
