import React, { useEffect, useState } from "react";
import api from "../ap/api.js";

function ApplicantDashboard() {
  const [applied, setApplied] = useState([]);
  const [saved, setSaved] = useState([]);

  useEffect(() => {
    loadApplications();
  }, []);

  const loadApplications = async () => {
    try {
      const appliedRes = await api.get("/applications/me?type=APPLIED");
      const savedRes = await api.get("/applications/me?type=SAVED");

      setApplied(appliedRes.data);
      setSaved(savedRes.data);
    } catch (err) {
      console.log("Error loading dashboard", err);
    }
  };

  return (
    <div className="dashboard-page">
      <h2>My Jobs</h2>

      <section>
        <h3>âœ… Applied Jobs</h3>
        {applied.length === 0 && <p>No applied jobs yet.</p>}
        {applied.map((app) => (
          <div key={app.id} className="job-card">
            <h4>{app.job.title}</h4>
            <p>{app.job.company} â€” {app.job.location}</p>
          </div>
        ))}
      </section>

      <section>
        <h3>ðŸ’¾ Saved Jobs</h3>
        {saved.length === 0 && <p>No saved jobs yet.</p>}
        {saved.map((app) => (
          <div key={app.id} className="job-card">
            <h4>{app.job.title}</h4>
            <p>{app.job.company} â€” {app.job.location}</p>
          </div>
        ))}
      </section>
    </div>
  );
}

export default ApplicantDashboard;
