import React from "react";
import { Link } from "react-router-dom";
import { useAuth } from "../context/AuthContext";

function Home() {
  const { user } = useAuth();

  return (
    <div className="hero">
      <div className="hero-overlay">
        <h1>Find The Right Job For Your Future</h1>
        <p>Your career starts here. Apply to jobs that match your passion.</p>

        {!user && (
          <Link to="/register" className="btn-primary hero-btn">
            Get Started
          </Link>
        )}

        {user && (
          <Link to="/jobs" className="btn-primary hero-btn">
            View Jobs
          </Link>
        )}
      </div>
    </div>
  );
}

export default Home;
