import React, { useState } from "react";
import { useNavigate, Link } from "react-router-dom";
const registerBg = process.env.PUBLIC_URL + "/assets/register-bg.jpg";


function Register() {
  const [form, setForm] = useState({ name: "", email: "", password: "", role: "applicant" });
  const nav = useNavigate();

  const handleSubmit = (e) => {
    e.preventDefault();
    alert("Registered successfully!");
    nav("/login");
  };

  return (
    <div className="auth-page" style={{ backgroundImage: `url(${registerBg})` }}>
      <div className="auth-container">
        <h2>Create an Account</h2>
        <p>Start your journey with JobConnect.</p>
        <form onSubmit={handleSubmit}>
          <input placeholder="Full Name" value={form.name} onChange={(e) => setForm({ ...form, name: e.target.value })} required />
          <input type="email" placeholder="Email" value={form.email} onChange={(e) => setForm({ ...form, email: e.target.value })} required />
          <input type="password" placeholder="Password" value={form.password} onChange={(e) => setForm({ ...form, password: e.target.value })} required />
          <select value={form.role} onChange={(e) => setForm({ ...form, role: e.target.value })}>
            <option value="applicant">Applicant</option>
            <option value="employer">Employer</option>
          </select>
          <button type="submit" className="btn-primary">Register</button>
        </form>
        <p className="auth-link">Already registered? <Link to="/login">Login</Link></p>
      </div>
    </div>
  );
}

export default Register;
