// src/components/Navbar.js
import React from "react";
import { Link, useNavigate } from "react-router-dom";
import { useAuth } from "../context/AuthContext";
import "./Navbar.css"; // keep styling if you have

export default function Navbar() {
  const navigate = useNavigate();
  const { isAuthenticated, user, logout } = useAuth();

  const handleLogout = () => {
    logout();
    navigate("/login");
  };

  return (
    <nav className="navbar">
      <div className="nav-content">
        <div className="nav-brand">
          <Link to="/">JobConnect</Link>
        </div>

        <div className="nav-links">
          <Link to="/jobs">Jobs</Link>
          {isAuthenticated && user?.role === "APPLICANT" && (
            <>
              <Link to="/applicant/dashboard">Dashboard</Link>
              <Link to="/applicant/applications">Applications</Link>
              <Link to="/applicant/saved-jobs">Saved Jobs</Link>
            </>
          )}
          {isAuthenticated && user?.role === "EMPLOYER" && (
            <>
              <Link to="/employer/dashboard">Dashboard</Link>
              <Link to="/employer/post-job">Post Job</Link>
              <Link to="/employer/manage-jobs">Manage Jobs</Link>
            </>
          )}
        </div>

        <div className="nav-actions">
          {isAuthenticated ? (
            <>
              <Link to="/profile" className="nav-user">
                {user?.name || "Profile"}
              </Link>
              <button type="button" className="nav-logout" onClick={handleLogout}>
                Logout
              </button>
            </>
          ) : (
            <>
              <Link to="/login" className="nav-login">
                Log in
              </Link>
              <Link to="/register" className="nav-register">
                Sign up
              </Link>
            </>
          )}
        </div>
      </div>
    </nav>
  );
}
